        if (prompt) {
            setTempNoteText(prompt)
        }
    }, [prompt])

    React.useEffect(() => {
        const timeoutId = setTimeout(() => {
            if (tempNoteText !== currentNote.body) {
                updateNote(tempNoteText)
            }
        }, 500)
        return () => clearTimeout(timeoutId)
    }, [tempNoteText])

    function handleChange(event){
        const {value} = event.target
        setPrompt(value)
