import React from 'react';
import SearchResult from './SearchResult'; // Assuming SearchResult component is in a separate file

export default function Header() {
    const [tempNoteText, setTempNoteText] = React.useState("");
    const [prompt, setPrompt] = React.useState("");
    const [searchResults, setSearchResults] = React.useState([]);

    function updateResults() {
        fetch(`https://api.quotable.io/search/quotes?query=${encodeURIComponent(prompt)}&fields=content`)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                console.log(data);
                setSearchResults(data);
            })
            .catch(error => {
                console.error('There was a problem with your fetch operation:', error);
            });
    }

    React.useEffect(() => {
        if (prompt) {
            setTempNoteText(prompt);
        }
    }, [prompt]);

    React.useEffect(() => {
        const timeoutId = setTimeout(() => {
            if (tempNoteText !== prompt) {
                updateResults(tempNoteText);
            }
        }, 500);
        return () => clearTimeout(timeoutId);
    }, [tempNoteText]);

    function handleChange(event) {
        const { value } = event.target;
        setPrompt(value);
        console.log(value);
    }

    return (
        <div>
            <nav>
                <input type="text" placeholder="Search..." value={prompt} name="prompt" onChange={handleChange} />
            </nav>
            <SearchResult results={searchResults} />
        </div>
    );
}
