import React from "react";


export default function Header(){
    const [tempNoteText, setTempNoteText] = React.useState("")
    const [prompt, setPrompt] = React.useState("")

    function updateResults(){
        fetch(`https://api.quotable.io/search/quotes?query=${encodeURIComponent(query)}&fields=content`)
        .then(response => {
            if (!response.ok) {
              throw new Error('Network response was not ok');
            }
            return response.json();
          })
          .then(data => {
            console.log(data);
            data.forEach(quote => {
              console.log(quote.content);
            });
          })
          .catch(error => {
            console.error('There was a problem with your fetch operation:', error);
          });
    }

    React.useEffect(() => {
        if (prompt) {
            setTempNoteText(prompt)
        }
    }, [prompt])

    React.useEffect(() => {
        const timeoutId = setTimeout(() => {
            if (tempNoteText !== prompt) {
                updateResults(tempNoteText)
            }
        }, 500)
        return () => clearTimeout(timeoutId)
    }, [tempNoteText])

    function handleChange(event){
        const {value} = event.target
        setPrompt(value)
        console.log(value)
    }

    return (
        <nav>
            <input type="text" placeholder="Search..." value={prompt} name="prompt" />
        </nav>
    )
}
